import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'GHE_Assignment';
  images = [
    { src: 'assets/images/skin-talk-img-1.png', alt: 'Skin Talk Image 1' },
    { src: 'assets/images/skin-talk-img-2.png', alt: 'Skin Talk Image 2' },
    { src: 'assets/images/skin-talk-img-3.png', alt: 'Skin Talk Image 3' },
    { src: 'assets/images/skin-talk-img-4.png', alt: 'Skin Talk Image 4' },
  ];
}
<!-- <app-home></app-home> -->
<div class="image-slider">
  <div class="image-wrapper" *ngFor="let image of images; index as i">
    <img [src]="image.src" [alt]="image.alt">
  </div>
</div>
<router-outlet></router-outlet>
